<?php    class MESSAGE {        private $MessageCode;                public function __construct($FileName) {            if(file_exists($FileName)) {                include $FileName;            }            else {                 echo '<p class="ErrorMessage">Nie odnaleziono pliku '.$FileName.'</p>';            }            $this->MessageCode = $MessageCode;        }                public function ViewMessage($Code) {            if(!is_object($Code)) {                if($Code !== NULL && $Code !== TRUE){                    if(array_key_exists($Code, $this->MessageCode)){                        $pattern = '/^S+[0-9]+$/';                        if(preg_match($pattern, $Code)) {                            echo '<p class="SuccessMessage">'.$this->MessageCode[$Code].'</p>';                        }                        else {                            echo '<p class="ErrorMessage">'.$this->MessageCode[$Code].'</p>';                        }                    }                    else {                        echo '<p class="ErrorMessage">Podany niepoprawny kod błędu: '.$Code.'</p>';                    }                }                else {                    return TRUE;                }            }            else {                return TRUE;            }        }    }?>