<?php    class USER {        private $Password;        public function GenerateHashPassword($Password, $ActivationCode) {            $this->Password = md5($Password.$ActivationCode);            return $this->Password;        }        public function Login($Email) {            if($_SESSION['Logged'] == TRUE) {                session_regenerate_id();                $_SESSION = array();            }            else {                $_SESSION['Logged'] = TRUE;                $_SESSION['IP'] = $_SERVER['REMOTE_ADDR'];                $_SESSION['UserAgent'] = $_SERVER['HTTP_USER_AGENT'];                $_SESSION['Email'] = $Email;            }            return 'S70';        }                public function Logout() {            if($_SESSION['Logged'] == TRUE) {                $_SESSION = array();                session_destroy();                                return 'S71';            }        }                public function CheckLogin() {            if($_SESSION['Logged'] == TRUE) {                if($_SESSION['IP'] != $_SERVER['REMOTE_ADDR'] || $_SESSION['UserAgent'] != $_SERVER['HTTP_USER_AGENT']) {                    $_SESSION = array();                    session_destroy();                                        return FALSE;                }                else {                    echo $_SESSION['Email'];                    return TRUE;                }            }            return FALSE;        }    }?>