<?php    class DATABASE {        private $Connection;        public function Connect($DATABASE_HOST, $DATABASE_USER, $DATABASE_PASSWORD, $DATABASE_NAME, $DATABASE_CHARSET) {            $this->Connection = new mysqli($DATABASE_HOST, $DATABASE_USER, $DATABASE_PASSWORD, $DATABASE_NAME);            if(mysqli_connect_error()) {                return 'E10';            }            else {                $this->Connection->set_charset($DATABASE_CHARSET);                return TRUE;            }        }                public function Close() {            if($this->Connection->close()) {                return TRUE;            }            else {                return 'E11';            }        }        public function QuerySetValue($Query) {            if($this->Connection->query($Query) === TRUE) {                return TRUE;            }            else {                return 'E12';            }        }        public function QueryGetValue($Query){            if($Result = $this->Connection->query($Query)) {                return $Result;            }            else {                return 'E12';            }        }                public function RealEscapeString($RealEscapeString) {            return $this->Connection->real_escape_string($RealEscapeString);        }    }?>