<?php    $DATABASE = new DATABASE();    $SHOWMESSAGE = new SHOWMESSAGE();    $SHOWMESSAGE->LoadMessageCode('settings/MessageCode.php');    $CHECKINPUT = new CHECKINPUT();      $Name = $_GET['UserName'];    $ActivationCode = $_GET['ActivationCode'];        if($CHECKINPUT->CheckLenght($ActivationCode, 32, 32) == FALSE) {        $SHOWMESSAGE->ViewMessage('E50');    }    else {        $SHOWMESSAGE->ViewMessage($DATABASE->Connect(DATABASE_HOST, DATABASE_USER, DATABASE_PASSWORD, DATABASE_NAME, DATABASE_CHARSET));            $Query = "SELECT ID, ACTIVE_ACCOUNT_ADMIN FROM CKPiDZ_Users WHERE ACTIVATION_CODE = '".$DATABASE->RealEscapeString($ActivationCode)."' AND EMAIL = '".$DATABASE->RealEscapeString($Name)."'";        $SHOWMESSAGE->ViewMessage($Result = $DATABASE->QueryGetValue($Query));        if($Result->num_rows != 1){            $SHOWMESSAGE->ViewMessage('E50');        }        else {            $Object = $Result->fetch_object();            $Query = "UPDATE CKPiDZ_Users SET ACTIVATION_CODE = '1', ACTIVE_ACCOUNT_USER = '1' WHERE ID = '".$Object->ID."'";            $SHOWMESSAGE->ViewMessage($DATABASE->QuerySetValue($Query));            if($Object->ACTIVE_ACCOUNT_ADMIN == 0){                $SHOWMESSAGE->ViewMessage('S50');            }            else {                $SHOWMESSAGE->ViewMessage('S51');            }        }        $Result->free;        $SHOWMESSAGE->ViewMessage($DATABASE->Close());    }?>